import { useMemo, useState } from "react";
export default function useBooleanState(initial = false) {
    const [state, setState] = useState(initial);
    const setBooleanState = useMemo(() => {
        const setValue = (v) => setState(v);
        setValue.toggle = () => setState((x) => !x);
        setValue.toFalse = () => setState(false);
        setValue.toTrue = () => setState(true);
        return setValue;
    }, []);
    return [state, setBooleanState];
}
